           $(this).addClass('animate');            $(this).find('div.carouselNext, div.carouselPrev').addClass('visible');        }, function () {            $(this).removeClass('animate');            $(this).parent().css('z-index', '1');            $(this).find('div.carouselNext, div.carouselPrev').removeClass('visible');        });    });        $('.add_to_cart').click(function () {        var productCard = $(this).parent();        var position = productCard.offset();        var productImage = $(productCard).find('img').get(0).src;        var youtubeID = $(productCard).find('.product_name').attr('id');        var productName = $(productCard).find('.product_name').get(0).innerHTML;        $('body').append('<div class="floating-cart"></div>');        var cart = $('div.floating-cart');        productCard.clone().appendTo(cart);        $(cart).css({            'top': position.top + 'px',            'left': position.left + 'px'        }).fadeIn('slow').addClass('moveToCart');        setTimeout(function () {            $('body').addClass('MakeFloatingCart');        }, 800);        setTimeout(function () {            $('div.floating-cart').remove();            $('body').removeClass('MakeFloatingCart');            var cartItem = '<div class=\'cart-item\'><div class=\'img-wrap\'><img src=\'' + productImage + '\' alt=\'\' /></div><span>' + productName + '</span><input type=\'hidden\' name=\'YoutubeID[]\' value=\''+ youtubeID +'\'><input type=\'hidden\' name=\'YoutubeTitle[]\' value=\''+ productName +'\'><div class=\'delete-item\'><i class=\'fa fa-times\'></i></div></div>';            $('#cart .empty').hide();            $('#cart').append(cartItem);            $('#checkout').fadeIn(500);            $('#playlistname-input').fadeIn(500);            $('.sortable').sortable();            $('#cart .cart-item').last().addClass('flash').find('.delete-item').click(function () {                $(this).parent().fadeOut(300, function () {                    $(this).remove();                    console.log($('#cart .cart-item').size());                    if ($('#cart .cart-item').size() == 0) {                        $('#cart .empty').fadeIn(500);                        $('#checkout').fadeOut(500);                        $('#playlistname-input').fadeOut(500);                    }                });            });            setTimeout(function () {                $('#cart .cart-item').last().removeClass('flash');            }, 10);        }, 1000);        })      }///    });  }</script>       <div id="fb-root"></div>    <script type="text/javascript">      window.fbAsyncInit = function() {        FB.init({          appId: '<?php echo $appId; ?>',          cookie: true,xfbml: true,          channelUrl: '<?php echo $return_url; ?>channel.php',          oauth: true          });        };    (function() {      var e = document.createElement('script');      e.async = true;e.src = document.location.protocol +'//connect.facebook.net/en_US/all.js';      document.getElementById('fb-root').appendChild(e);}());    function CallAfterLogin(){      FB.login(function(response) {           if (response.status === "connected")         {          LodingAnimate(); //Animate login          FB.api('/me', function(data) {                        // if(data.email == null)            // {              // //Facbeook user email is empty, you can check something like this.              // alert("You must allow us to access your email id!");               // ResetAnimate();            // }else{              AjaxResponse();            // }                      });         }      },      {scope:'<?php echo $fbPermissions; ?>'});    }    //functions    function AjaxResponse()    {       //Load data from the server and place the returned HTML into the matched element using jQuery Load().       $( "#results" ).load( "process_facebook.php" );    }    //Show loading Image    function Lod