tingCart');            var cartItem = '<div class=\'cart-item\'><div class=\'img-wrap\'><img src=\'' + productImage + '\' alt=\'\' /></div><span>' + productName + '</span><input type=\'hidden\' name=\'YoutubeID[]\' value=\''+ youtubeID +'\'><input type=\'hidden\' name=\'YoutubeTitle[]\' value=\''+ productName +'\'><div class=\'delete-item\'><i class=\'fa fa-times\'></i></div></div>';            $('#cart .empty').hide();            $('#cart').append(cartItem);            $('#checkout').fadeIn(500);            $('#playlistname-input').fadeIn(500);            $('.sortable').sortable();            $('#cart .cart-item').last().addClass('flash').find('.delete-item').click(function () {                $(this).parent().fadeOut(300, function () {                    $(this).remove();                    console.log($('#cart .cart-item').size());                    if ($('#cart .cart-item').size() == 0) {                        $('#cart .empty').fadeIn(500);                        $('#checkout').fadeOut(500);                        $('#playlistname-input').fadeOut(500);                    }                });            });            setTimeout(function () {                $('#cart .cart-item').last().removeClass('flash');            }, 10);        }, 1000);        })      }///    });  }  </script>   <script>  (function(i,s,o,g,r,a,m){i['GoogleAnalyticsObject']=r;i[r]=i[r]||function(){  (i[r].q=i[r].q||[]).push(arguments)},i[r].l=1*new Date();a=s.createElement(o),  m=s.getElementsByTagName(o)[0];a.async=1;a.src=g;m.parentNode.insertBefore(a,m)  })(window,document,'script','//www.google-analytics.com/analytics.js','ga');  ga('create', 'UA-68434550-1', 'auto');  ga('send', 'pageview');</script></body></html>